package week1.day2;

public class CheckIfNumberIsPositiveOrNegative {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num = 35;
		
		if (num>0) {
			
			System.out.println("The number is postive: "+num);
			
		} else if (num<0) {
			
			System.out.println("The number is negative:"+num);
			
		}
		
		
		else {
			
			System.out.println("The number is neither postive nor negative");

		}

	}

}
