package week1.day2;

public class EvenOdd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int number=56538;
		
		if(number%2==0) {
			
			System.out.println("Even number: "+number);
			
		}
		else if (number%2!=0) {
			System.out.println("Odd number: "+number);
		}
		else {
			System.out.println("Not a number");
		}

	}

}
