package week1.day2;

import java.util.Iterator;

public class Factorial {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int fact=1;
		int i;
		
	for (i = 5; i >1; i--) {
		
		
		System.out.print(i+"*");
		
		fact=fact*i;
		
	}
	
	System.out.println(i+"*"+"="+fact);
	

	}

}
